project/contracts/GhostCredits.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/// @title GhostCredits
/// @notice Treasury and credit tracker for GhostGate V2 API monetization.
/// @dev API signing/verification is handled off-chain. This contract stores credits and settlements.
contract GhostCredits is Ownable, ReentrancyGuard {
    uint256 public constant BPS_DENOMINATOR = 10_000;

    /// @notice Price per credit in wei.
    uint256 public pricePerCredit;

    /// @notice Platform fee in basis points. 500 = 5%.
    uint256 public platformFeeBps = 500;

    /// @notice User credit balances.
    mapping(address => uint256) public credits;

    /// @notice Provider earnings pending claim (in wei).
    mapping(address => uint256) public earnings;

    /// @notice Total fees accrued and available for owner withdrawal.
    uint256 public accumulatedFees;

    /// @notice Sum of all unclaimed provider earnings.
    uint256 public totalPendingEarnings;

    event CreditsPurchased(address indexed user, uint256 amount);
    event UsageSettled(address indexed user, address indexed provider, uint256 amount);
    event EarningsClaimed(address indexed provider, uint256 netAmount, uint256 fee);

    error InvalidPrice();
    error InvalidAddress();
    error InvalidAmount();
    error InsufficientCredits();
    error InsufficientEarnings();
    error InsolventTreasury();
    error TransferFailed();

    /// @param initialPricePerCredit Price per credit in wei (must be > 0).
    /// @param initialOwner Address to receive owner privileges.
    constructor(uint256 initialPricePerCredit, address initialOwner) Ownable(initialOwner) {
        if (initialOwner == address(0)) revert InvalidAddress();
        if (initialPricePerCredit == 0) revert InvalidPrice();
        pricePerCredit = initialPricePerCredit;
    }

    /// @notice Buy credits with ETH.
    /// @dev msg.value must be an exact multiple of pricePerCredit.
    function buyCredits() external payable {
        if (msg.value == 0) revert InvalidAmount();

        uint256 unitPrice = pricePerCredit;
        if (unitPrice == 0) revert InvalidPrice();
        if (msg.value % unitPrice != 0) revert InvalidAmount();

        uint256 amount = msg.value / unitPrice;
        if (amount == 0) revert InvalidAmount();

        credits[msg.sender] += amount;
        emit CreditsPurchased(msg.sender, amount);
    }

    /// @notice Settle user API consumption and allocate provider earnings.
    /// @param user User whose credits are deducted.
    /// @param amountSpent Credits to deduct from the user.
    /// @param provider Provider to credit with earnings.
    /// @param amountEarned Earnings (wei) to add for the provider.
    function settleUsage(
        address user,
        uint256 amountSpent,
        address provider,
        uint256 amountEarned
    ) external onlyOwner {
        if (user == address(0) || provider == address(0)) revert InvalidAddress();
        if (amountSpent == 0 || amountEarned == 0) revert InvalidAmount();

        uint256 userCredits = credits[user];
        if (userCredits < amountSpent) revert InsufficientCredits();

        // Prevent allocating liabilities that exceed available treasury backing.
        uint256 requiredBacking = totalPendingEarnings + accumulatedFees + amountEarned;
        if (address(this).balance < requiredBacking) revert InsolventTreasury();

        credits[user] = userCredits - amountSpent;
        earnings[provider] += amountEarned;
        totalPendingEarnings += amountEarned;

        // "amount" in event refers to credits consumed for this settlement.
        emit UsageSettled(user, provider, amountSpent);
    }

    /// @notice Claim provider earnings minus platform fee.
    function claimEarnings() external nonReentrant {
        uint256 amount = earnings[msg.sender];
        if (amount == 0) revert InsufficientEarnings();

        uint256 fee = (amount * platformFeeBps) / BPS_DENOMINATOR;
        uint256 netAmount = amount - fee;

        // Effects first.
        earnings[msg.sender] = 0;
        totalPendingEarnings -= amount;
        accumulatedFees += fee;

        (bool ok, ) = payable(msg.sender).call{value: netAmount}("");
        if (!ok) revert TransferFailed();

        emit EarningsClaimed(msg.sender, netAmount, fee);
    }

    /// @notice Withdraw only accrued platform fees.
    function withdrawFees() external onlyOwner nonReentrant {
        uint256 amount = accumulatedFees;
        if (amount == 0) revert InvalidAmount();

        accumulatedFees = 0;
        (bool ok, ) = payable(owner()).call{value: amount}("");
        if (!ok) revert TransferFailed();
    }

    /// @notice Update credit price.
    /// @param newPricePerCredit New price per credit in wei.
    function setPricePerCredit(uint256 newPricePerCredit) external onlyOwner {
        if (newPricePerCredit == 0) revert InvalidPrice();
        pricePerCredit = newPricePerCredit;
    }
}
